package com.mulesoft.mulecxfexceptionexample;

import org.junit.Test;
import org.mule.api.MuleMessage;
import org.mule.module.client.MuleClient;
import org.mule.tck.FunctionalTestCase;
import org.mule.transport.NullPayload;

public class MulecxfexceptionexampleTestCase extends FunctionalTestCase
{
    protected String getConfigResources()
    {
        return "mulecxfexceptionexample-functional-test-config.xml";
    }

    @Test
    public void testSessionCacheAndCXFWithSmallMessage() throws Exception
    {
        MuleClient client = new MuleClient(muleContext);
        MuleMessage result = client.send("vm://in","RRR", null);
        assertNotNull(result);
        assertNull(result.getExceptionPayload());
        assertFalse(result.getPayload() instanceof NullPayload);
        String response = result.getPayloadAsString();
        assertEquals("RRR", result.getPayloadAsString());
    }

    @Test
    public void testSessionCacheAndCXFWithLargeMessage() throws Exception
    {
        MuleClient client = new MuleClient(muleContext);
        MuleMessage result = client.send("vm://in", "<?xml version=\"1.0\"?><ns1:GetServiceStatusResponse xmlns:ns1=\"http://fba-inbound.amazonaws.com/doc/2007-05-10/\"><ns1:GetServiceStatusResult></ns1:GetServiceStatusResult></ns1:GetServiceStatusResponse>", null);
        assertNotNull(result);
        assertNotNull(result.getExceptionPayload());
        assertTrue(result.getPayload() instanceof NullPayload);
        //assertEquals("<?xml version=\"1.0\"?><ns1:GetServiceStatusResponse xmlns:ns1=\"http://fba-inbound.amazonaws.com/doc/2007-05-10/\"><ns1:GetServiceStatusResult></ns1:GetServiceStatusResult></ns1:GetServiceStatusResponse>", result.getPayloadAsString());
    }
}
